const initialState = {
  jobs: {
    content: [],
  },
  companys: {
    content: [],
  },
};

const mainReducer = (state = initialState, action) => {
  switch (action.type) {
    case "SET_JOBS":
      return { ...state, jobs: { ...state.jobs, content: action.payload } };
    default:
      return state;
  }
};
export default mainReducer;
